import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import { makeStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    flexDirection: "column",
    flexBasis: "30%",
    padding: "1rem",
    paddingTop: "2rem",
    boxShadow: "none",
    ["@media (max-width:800px)"]: {
      width: "100%",
      padding: "0",
    },
  },
  header: {
    textAlign: "center",
  },
  media: {
    height: "12rem",
    width: "20rem",
    ["@media (min-width:800px)"]: {
      height: "12rem",
    },
  },
  content: {
    display: "flex",
    flexDirection: "column",
  },
  rowCont: {
    display: "flex",
    justifyContent: "space-between",
    ["@media (min-width:800px)"]: {
      display: "block",
    },
  },
  subCont: {
    width: "100%",
    paddingLeft: "0.5rem",
    ["@media (max-width:800px)"]: {
      width: "50%",
    },
  },
  league: {
    textAlign: "center",
    paddingTop: "1rem",
  },
}));
var Cards = (props) => {
  const classes = useStyles();
  return (
    <Card className={classes.root}>
      <CardHeader className={classes.header} title={props.name} />
      <CardMedia className={classes.media} image={props.flag} />
      <CardContent className={classes.content}>
        <div className={classes.rowCont}>
          <div className={classes.subCont}>
            <Typography variant="body2">
              <span className={classes.heading}>Capital: </span>
              {props.capital}
            </Typography>
          </div>
          <div className={classes.subCont}>
            <Typography variant="body2">
              <span className={classes.heading}>Region: </span>
              {props.region}
            </Typography>
          </div>
        </div>
        <div className={classes.rowCont}>
          <div className={classes.subCont}>
            <Typography variant="body2">
              <span className={classes.heading}>Sub Region: </span>
              {props.subRegion}
            </Typography>
          </div>
          <div className={classes.subCont}>
            <Typography variant="body2">
              <span className={classes.heading}>Population: </span>
              {props.population}
            </Typography>
          </div>
        </div>
        <div className={classes.rowCont}>
          <div className={classes.subCont}>
            <Typography variant="body2">Area:{props.area} km</Typography>
          </div>
          <div className={classes.subCont}>
            <Typography variant="body2">
              <span className={classes.heading}>Borders: </span>
              {props.borders.map((i) => {
                return <span> {i},</span>;
              })}
            </Typography>
          </div>
        </div>
        <div className={classes.rowCont}>
          <div className={classes.subCont}>
            <Typography variant="body2">
              <span className={classes.heading}>Native name: </span>
              {props.nativeName}
            </Typography>
          </div>
          <div className={classes.subCont}>
            <Typography variant="body2">
              <span className={classes.heading}>Languages:</span>
              {props.languages.map((i) => {
                return <span> {i.name},</span>;
              })}
            </Typography>
          </div>
        </div>
        <div className={classes.rowCont}>
          <div className={classes.subCont}>
            <Typography variant="body2">
              <span className={classes.heading}>Calling Codes: </span>
              {props.callingCode.map((i) => {
                return <span> {i},</span>;
              })}
            </Typography>
          </div>
          <div className={classes.subCont}>
            <Typography variant="body2">
              <span className={classes.heading}>Regional Blocks: </span>{" "}
              className={classes.heading}
              {props.regionalBlocks.map((i) => {
                return <span> {i.name},</span>;
              })}
            </Typography>
          </div>
        </div>
      </CardContent>
    </Card>
  );
};
export default Cards;
