import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import { makeStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    flexDirection: "column",
    flexBasis: "33%",
    boxShadow: "none",
    marginBottom: "2rem",
    backgroundColor: "#e4fbff",
    ["@media (min-width:800px)"]: {
      width: "100%",
      padding: "0",
    },
  },
  header: {
    textAlign: "center",
  },
  heading: {
    color: "#02475e",
    fontWeight: "600",
  },

  media: {
    height: "12rem",
    width: "20rem",
    ["@media (max-width:800px)"]: {
      margin: "auto",
    },
  },
  content: {
    display: "flex",
    flexDirection: "column",
    ["@media (max-width:800px)"]: {
      paddingLeft: "10rem",
      width: "100%",
    },
  },
  rowCont: {
    display: "block",
    textAlign: "left",
  },
  league: {
    textAlign: "center",
    paddingTop: "1rem",
  },
}));
var Cards = (props) => {
  const classes = useStyles();
  return (
    <Card className={classes.root}>
      <CardHeader className={classes.header} title={props.name} />
      <CardMedia className={classes.media} image={props.flag} />
      <CardContent className={classes.content}>
        <Typography variant="body2">
          <span className={classes.heading}>Capital: </span>
          {props.capital}
        </Typography>
        <div className={classes.subCont}>
          <Typography variant="body2">
            <span className={classes.heading}>Region: </span>
            {props.region}
          </Typography>
        </div>

        <Typography variant="body2">
          <span className={classes.heading}>Sub Region: </span>
          {props.subRegion}
        </Typography>

        <div className={classes.subCont}>
          <Typography variant="body2">
            <span className={classes.heading}>Population: </span>
            {props.population}
          </Typography>
        </div>

        <div className={classes.rowCont}>
          <div className={classes.subCont}>
            <Typography variant="body2">
              <span className={classes.heading}>Area: </span>
              {props.area} km
            </Typography>
          </div>
          <div className={classes.subCont}>
            <Typography variant="body2">
              <span className={classes.heading}>Borders: </span>
              {props.borders.map((i) => {
                return <span> {i},</span>;
              })}
            </Typography>
          </div>
        </div>
        <div className={classes.rowCont}>
          <div className={classes.subCont}>
            <Typography variant="body2">
              <span className={classes.heading}>Native name: </span>
              {props.nativeName}
            </Typography>
          </div>
          <div className={classes.subCont}>
            <Typography variant="body2">
              <span className={classes.heading}>Languages:</span>
              {props.languages.map((i) => {
                return <span> {i.name},</span>;
              })}
            </Typography>
          </div>
        </div>
        <div className={classes.rowCont}>
          <div className={classes.subCont}>
            <Typography variant="body2">
              <span className={classes.heading}>Calling Codes: </span>
              {props.callingCode.map((i) => {
                return <span> {i},</span>;
              })}
            </Typography>
          </div>
          <div className={classes.subCont}>
            <Typography variant="body2">
              <span className={classes.heading}>Regional Blocks: </span>
              {props.regionalBlocks.map((i) => {
                return <span> {i.name},</span>;
              })}
            </Typography>
          </div>
        </div>
      </CardContent>
    </Card>
  );
};
export default Cards;
